SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_reg_e200_icmsipi (nr_seq_controle_p bigint) AS $body$
DECLARE


/*REGISTRO E200: PERÍODO DA APURAÇÃO DO ICMS.*/



-- VARIABLES
ie_gerou_dados_bloco_w varchar(1) := 'N';

nr_seq_icmsipi_E200_w fis_efd_icmsipi_E200.nr_sequencia%type;

qt_cursor_w bigint := 0;
nr_vetor_w  bigint := 0;

-- USUARIO
nm_usuario_w usuario.nm_usuario%type;

/*Cursor que retorna as informações para o registro E200 restringindo pela sequencia da nota fiscal*/
c_reg_E200 CURSOR FOR
	SELECT 	distinct CASE WHEN c.sg_estado='IN' THEN  'EX'  ELSE c.sg_estado END  sg_uf,
		trunc(d.dt_inicio_apuracao) dt_ini,
		trunc(d.dt_fim_apuracao) dt_fin
	from 	fis_efd_icmsipi_c100     a,
		pessoa_fisica            b,
		compl_pessoa_fisica      c,
		fis_efd_icmsipi_controle d,
		fis_efd_icmsipi_lote     e
	where 	a.cd_part = b.cd_pessoa_fisica
	and 	b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and 	d.nr_seq_lote = e.nr_sequencia
	and 	a.nr_seq_controle = d.nr_sequencia
	and 	d.nr_sequencia = nr_seq_controle_p
	and		(c.sg_estado IS NOT NULL AND c.sg_estado::text <> '')
	
union

	SELECT 	distinct CASE WHEN b.sg_estado='IN' THEN  'EX'  ELSE b.sg_estado END  sg_uf,
		trunc(c.dt_inicio_apuracao) dt_ini,
		trunc(c.dt_fim_apuracao) dt_fin
	from 	fis_efd_icmsipi_c100 a, 
		pessoa_juridica b,
		fis_efd_icmsipi_controle c,
		fis_efd_icmsipi_lote     d
	where 	a.cd_part = b.cd_cgc
	and 	c.nr_seq_lote = d.nr_sequencia
	and 	a.nr_seq_controle = c.nr_sequencia
	and 	c.nr_sequencia = nr_seq_controle_p
	and		(b.sg_estado IS NOT NULL AND b.sg_estado::text <> '')
    
union

    select distinct CASE WHEN obter_dados_pf_pj(null, obter_cgc_estabelecimento(c.cd_estabelecimento), 'UF')='IN' THEN  'EX'  ELSE obter_dados_pf_pj(null, obter_cgc_estabelecimento(c.cd_estabelecimento), 'UF') END  sg_uf,
		trunc(c.dt_inicio_apuracao) dt_ini,
		trunc(c.dt_fim_apuracao) dt_fin
    from fis_efd_icmsipi_controle c
	where 	c.nr_sequencia = nr_seq_controle_p
	and		obter_dados_pf_pj(null, obter_cgc_estabelecimento(c.cd_estabelecimento), 'UF') is not null;
	

/*Criação do array com o tipo sendo do cursor eespecificado - c_reg_E200*/

type reg_c_reg_E200 is table of c_reg_E200%RowType;
vetRegE200 reg_c_reg_E200;

/*Criação do array com o tipo sendo da tabela eespecificada - FIS_EFD_ICMSIPI_E200 */

type registro is table of fis_efd_icmsipi_E200%rowtype index by integer;
fis_registros_w registro;

BEGIN
/*Obteção do usuário ativo no tasy*/

nm_usuario_w := Obter_Usuario_Ativo;

open c_reg_E200;
loop
fetch c_reg_E200 bulk collect into vetRegE200 limit 1000;
	for i in 1 .. vetRegE200.Count loop
	begin

	/*Incrementa a variavel para o array*/

	qt_cursor_w:=	qt_cursor_w + 1;

	if (ie_gerou_dados_bloco_w = 'N') then
	  ie_gerou_dados_bloco_w := 'S';
	end if;

	/*Busca da sequencia da tabela especificada - fis_efd_icmsipi_E200 */

	select nextval('fis_efd_icmsipi_e200_seq')	  into STRICT nr_seq_icmsipi_E200_w	;
	
	--
	CALL fis_gerar_reg_E210_icmsipi(	nr_seq_controle_p,
					vetRegE200[i].sg_uf);

	/*Inserindo valores no array para realização do forall posteriormente*/
	fis_registros_w[qt_cursor_w].nr_sequencia 		:= nr_seq_icmsipi_E200_w;
	fis_registros_w[qt_cursor_w].dt_atualizacao 		:= clock_timestamp();
	fis_registros_w[qt_cursor_w].nm_usuario 		:= nm_usuario_w;
	fis_registros_w[qt_cursor_w].dt_atualizacao_nrec 	:= clock_timestamp();
	fis_registros_w[qt_cursor_w].nm_usuario_nrec 		:= nm_usuario_w;
	fis_registros_w[qt_cursor_w].cd_reg 			:= 'E200';
	fis_registros_w[qt_cursor_w].sg_uf 			:= vetRegE200[i].sg_uf;	
	fis_registros_w[qt_cursor_w].dt_ini 			:= vetRegE200[i].dt_ini;	
	fis_registros_w[qt_cursor_w].dt_fin 			:= vetRegE200[i].dt_fin;	
	fis_registros_w[qt_cursor_w].nr_seq_controle 		:= nr_seq_controle_p;

	if (nr_vetor_w >= 1000) then
	begin
	/*Inserindo registros definitivamente na tabela especifica - FIS_EFD_ICMSIPI_E200 */

	forall i in fis_registros_w.first .. fis_registros_w.last
		insert into fis_efd_icmsipi_E200 values fis_registros_w(i);

		nr_vetor_w := 0;
		fis_registros_w.delete;

		commit;

	end;
	end if;

	/*incrementa variavel para realizar o forall quando chegar no valor limite*/

	nr_vetor_w := nr_vetor_w + 1;

	end;
	end loop;
EXIT WHEN NOT FOUND; /* apply on c_reg_E200 */
end loop;
close c_reg_E200;

if (fis_registros_w.count > 0) then
begin
/*Inserindo registro que não entraram outro for all devido a quantidade de registros no vetor*/

forall i in fis_registros_w.first .. fis_registros_w.last
	insert into fis_efd_icmsipi_E200 values fis_registros_w(i);

	fis_registros_w.delete;

	commit;

end;
end if;

/*Libera memoria*/

dbms_session.free_unused_user_memory;

/*Atualização informação no controle de geração de registro para SIM*/

if (ie_gerou_dados_bloco_w = 'S') then
	update fis_efd_icmsipi_controle
	set ie_mov_E = 'S'
	where nr_sequencia = nr_seq_controle_p;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE fis_gerar_reg_e200_icmsipi (nr_seq_controle_p bigint) FROM PUBLIC;

