SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_reg_e300_icmsipi (nr_seq_controle_p bigint) AS $body$
DECLARE


/*REGISTRO E300: PERÍODO DA APURAÇÃO DO ICMS.*/

-- VARIABLES
ie_gerou_dados_bloco_w varchar(1) := 'N';

nr_seq_icmsipi_E300_w fis_efd_icmsipi_E300.nr_sequencia%type;

qt_cursor_w bigint := 0;
nr_vetor_w  bigint := 0;

-- USUARIO
nm_usuario_w usuario.nm_usuario%type;

/*Cursor que retorna as informações para o registro E300 restringindo pela sequencia da nota fiscal*/
c_reg_E300 CURSOR FOR
	SELECT 	distinct c.sg_estado sg_uf,
			trunc(d.dt_inicio_apuracao) dt_ini,
			trunc(d.dt_fim_apuracao) dt_fin
	from 	fis_efd_icmsipi_c100     a,
			fis_efd_icmsipi_c101 	aa,
			pessoa_fisica            b,
			compl_pessoa_fisica      c,
			fis_efd_icmsipi_controle d,
			fis_efd_icmsipi_lote     e
	where 	a.cd_part =  b.cd_pessoa_fisica
	and		aa.nr_seq_nota = a.nr_seq_nota
	and		aa.nr_seq_controle = a.nr_seq_controle
	and 	b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and 	d.nr_seq_lote = e.nr_sequencia
	and 	a.nr_seq_controle = d.nr_sequencia
	and 	d.nr_sequencia = nr_seq_controle_p
	and 	c.ie_tipo_complemento	= 1
	
union

	SELECT 	distinct b.sg_estado sg_uf,
			trunc(c.dt_inicio_apuracao) dt_ini,
			trunc(c.dt_fim_apuracao) dt_fin
	from 	fis_efd_icmsipi_c100 a, 
			fis_efd_icmsipi_c101 aa,
			pessoa_juridica b,
			fis_efd_icmsipi_controle c,
			fis_efd_icmsipi_lote     d
	where 	a.cd_part = b.cd_cgc
	and		aa.nr_seq_nota = a.nr_seq_nota
	and		aa.nr_seq_controle = a.nr_seq_controle
	and 	c.nr_seq_lote = d.nr_sequencia
	and 	a.nr_seq_controle = c.nr_sequencia
	and 	c.nr_sequencia = nr_seq_controle_p
	
union

	select	substr(obter_dados_pf_pj(null, b.cd_cgc, 'UF'),1,2) sg_uf,
			trunc(a.dt_inicio_apuracao) dt_ini,
			trunc(a.dt_fim_apuracao) dt_fin
	from	fis_efd_icmsipi_controle a,
			estabelecimento b,
			pessoa_juridica c				
	where	b.cd_cgc = c.cd_cgc
	and	a.nr_sequencia = nr_seq_controle_p
	and	b.cd_estabelecimento = a.cd_estabelecimento;

/*Criação do array com o tipo sendo do cursor eespecificado - c_reg_E300*/

type reg_c_reg_E300 is table of c_reg_E300%RowType;
vetRegE300 reg_c_reg_E300;

/*Criação do array com o tipo sendo da tabela eespecificada - FIS_EFD_ICMSIPI_E300 */

type registro is table of fis_efd_icmsipi_E300%rowtype index by integer;
fis_registros_w registro;

BEGIN
/*Obteção do usuário ativo no tasy*/

nm_usuario_w := Obter_Usuario_Ativo;

open c_reg_E300;
loop
fetch c_reg_E300 bulk collect into vetRegE300 limit 1000;
	for i in 1 .. vetRegE300.Count loop
	begin

	/*Incrementa a variavel para o array*/

	qt_cursor_w:=	qt_cursor_w + 1;

	if (ie_gerou_dados_bloco_w = 'N') then
	  ie_gerou_dados_bloco_w := 'S';
	end if;

	/*Busca da sequencia da tabela especificada - fis_efd_icmsipi_E300 */

	select nextval('fis_efd_icmsipi_e300_seq')	  into STRICT nr_seq_icmsipi_E300_w	;
	
	--
	CALL fis_gerar_reg_E310_icmsipi(	nr_seq_controle_p,
					vetRegE300[i].sg_uf);

	/*Inserindo valores no array para realização do forall posteriormente*/
	fis_registros_w[qt_cursor_w].nr_sequencia 			:= nr_seq_icmsipi_E300_w;
	fis_registros_w[qt_cursor_w].dt_atualizacao 		:= clock_timestamp();
	fis_registros_w[qt_cursor_w].nm_usuario 			:= nm_usuario_w;
	fis_registros_w[qt_cursor_w].dt_atualizacao_nrec 	:= clock_timestamp();
	fis_registros_w[qt_cursor_w].nm_usuario_nrec 		:= nm_usuario_w;
	fis_registros_w[qt_cursor_w].cd_reg 				:= 'E300';
	fis_registros_w[qt_cursor_w].sg_uf 					:= vetRegE300[i].sg_uf;	
	fis_registros_w[qt_cursor_w].dt_ini 				:= vetRegE300[i].dt_ini;	
	fis_registros_w[qt_cursor_w].dt_fin 				:= vetRegE300[i].dt_fin;	
	fis_registros_w[qt_cursor_w].nr_seq_controle 		:= nr_seq_controle_p;

	if (nr_vetor_w >= 1000) then
	begin
	/*Inserindo registros definitivamente na tabela especifica - FIS_EFD_ICMSIPI_E300 */

	forall i in fis_registros_w.first .. fis_registros_w.last
		insert into fis_efd_icmsipi_E300 values fis_registros_w(i);

		nr_vetor_w := 0;
		fis_registros_w.delete;

		commit;

	end;
	end if;

	/*incrementa variavel para realizar o forall quando chegar no valor limite*/

	nr_vetor_w := nr_vetor_w + 1;

	end;
	end loop;
EXIT WHEN NOT FOUND; /* apply on c_reg_E300 */
end loop;
close c_reg_E300;

if (fis_registros_w.count > 0) then
begin
/*Inserindo registro que não entraram outro for all devido a quantidade de registros no vetor*/

forall i in fis_registros_w.first .. fis_registros_w.last
	insert into fis_efd_icmsipi_E300 values fis_registros_w(i);

	fis_registros_w.delete;

	commit;

end;
end if;

/*Libera memoria*/

dbms_session.free_unused_user_memory;

/*Atualização informação no controle de geração de registro para SIM*/

if (ie_gerou_dados_bloco_w = 'S') then
	update fis_efd_icmsipi_controle
	set ie_mov_E = 'S'
	where nr_sequencia = nr_seq_controle_p;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE fis_gerar_reg_e300_icmsipi (nr_seq_controle_p bigint) FROM PUBLIC;

