CREATE OR REPLACE PROCEDURE fis_gerar_reg_fim_bloco_efd ( nr_seq_controle_p bigint, nm_usuario_p text, ds_separador_p text, cd_registro_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


cd_registro_w			varchar(15)	:= cd_registro_p;
ds_arquivo_w			varchar(4000);
ds_arquivo_compl_w		varchar(4000);
ds_linha_w			varchar(8000);
nr_linha_w			bigint := qt_linha_p;
nr_seq_registro_w			bigint := nr_sequencia_p;
sep_w				varchar(1)	:= ds_separador_p;
qt_linhas_bloco_w			bigint;


BEGIN

if (cd_registro_w = '9999') then
	begin
	select	count(*)
	into STRICT	qt_linhas_bloco_w
	from	fis_efd_arquivo
	where	nr_seq_controle_efd	= nr_seq_controle_p;
	end;
else
	begin
	select	count(*)
	into STRICT	qt_linhas_bloco_w
	from	fis_efd_arquivo
	where	nr_seq_controle_efd	= nr_seq_controle_p
	and	substr(cd_registro,1,1) = substr(cd_registro_w,1,1);

	if (cd_registro_w = '9990') then
		qt_linhas_bloco_w	:= qt_linhas_bloco_w + 1;
	end if;
	end;
end if;

qt_linhas_bloco_w	:= qt_linhas_bloco_w + 1; /* Soma das linhas mais a linha correspondente ao registro de encerramento */
ds_linha_w		:= substr(	sep_w || cd_registro_w		||
					sep_w || qt_linhas_bloco_w	|| sep_w ,1,8000);

ds_arquivo_w		:= substr(ds_linha_w,1,4000);
nr_seq_registro_w	:= nr_seq_registro_w + 1;
nr_linha_w		:= nr_linha_w + 1;

insert into fis_efd_arquivo(
	nr_sequencia,
	nm_usuario,
	dt_atualizacao,
	nm_usuario_nrec,
	dt_atualizacao_nrec,
	nr_seq_controle_efd,
	nr_linha,
	cd_registro,
	ds_arquivo,
	ds_arquivo_compl)
values (	nr_seq_registro_w,
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nr_seq_controle_p,
	nr_linha_w,
	cd_registro_w,
	ds_arquivo_w,
	ds_arquivo_compl_w);

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
