CREATE OR REPLACE PROCEDURE fis_gerar_reg_h010_efd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint, ie_mes_inventario_p bigint) AS $body$
DECLARE


nr_seq_regra_efd_w		bigint;
nr_versao_efd_w		varchar(5);
tp_registro_w		varchar(4);
dt_mesano_referencia_w	timestamp;
nr_linha_w		bigint := qt_linha_p;
nr_seq_registro_w		bigint := nr_sequencia_p;
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w		varchar(4000);
ds_linha_w		varchar(8000);
sep_w			varchar(1)	:= ds_separador_p;
nr_sequencia_w		bigint;

c01 CURSOR FOR
SELECT	'H010' tp_registro,
	a.cd_material,
	substr(b.cd_unidade_medida_estoque, 1, 6) cd_unidade_medida_estoque,
	coalesce(sum(a.qt_estoque),0) qt_estoque,
	coalesce(dividir(sum(a.vl_estoque), sum(a.qt_estoque)),0) vl_unitario,
	coalesce(sum(a.vl_estoque),0) vl_estoque,
	0 ie_prop_posse_item,
	'' cd_participante,
	'' ds_complemento,
	substr(obter_conta_contabil_material(a.cd_estabelecimento, a.cd_material), 1, 20) cd_conta_contabil
from	material b,
	saldo_estoque a
where	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.dt_mesano_referencia 	= dt_mesano_referencia_w
and	a.cd_material		= b.cd_material
group by	a.cd_material,
	b.cd_unidade_medida_estoque,
	a.cd_estabelecimento;

vet01	C01%RowType;


BEGIN

dt_mesano_referencia_w	:= trunc(add_months(trunc(dt_inicio_p, 'mm'), - ie_mes_inventario_p), 'mm');

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_linha_w	:= substr(	sep_w || vet01.tp_registro	 					||
					sep_w || vet01.cd_material					||
					sep_w || vet01.cd_unidade_medida_estoque			||
					sep_w || substr(sped_obter_campo_valor(vet01.qt_estoque), 1, 30)	||
					sep_w || substr(sped_obter_campo_valor(vet01.vl_unitario), 1, 30)	||
					sep_w || substr(sped_obter_campo_valor(vet01.vl_estoque), 1, 30)	||
					sep_w || vet01.ie_prop_posse_item				||
					sep_w || vet01.cd_participante				||
					sep_w || vet01.ds_complemento				||
					sep_w || vet01.cd_conta_contabil				||
					sep_w || 	'0'	||sep_w,1,8000);

	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;



	insert into fis_efd_arquivo(
		nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_controle_efd,
		nr_linha,
		cd_registro,
		ds_arquivo,
		ds_arquivo_compl)
	values (	nr_seq_registro_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_controle_p,
		nr_linha_w,
		vet01.tp_registro,
		ds_arquivo_w,
		ds_arquivo_compl_w);
	end;
end loop;
close C01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
