CREATE OR REPLACE PROCEDURE fis_gerar_reg_header_dipj ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE



dt_ano_w			timestamp;
ds_sistema_w			varchar(4)	:= 'DIPJ';
tp_registro_w			varchar(4);
nr_linha_w			bigint 	:= qt_linha_p;
nr_seq_registro_w		bigint 	:= nr_sequencia_p;
ds_arquivo_w			varchar(4000);
ds_compl_arquivo_w		varchar(4000);
ds_linha_w			varchar(8000);
sep_w				varchar(1) 	:= '';


BEGIN

/*Busca as informações*/

select	max(a.dt_ano)
into STRICT	dt_ano_w
from	fis_regra_dipj a,
	fis_controle_dipj b
where	b.nr_seq_regra_dipj	= a.nr_sequencia
and	b.nr_sequencia		= nr_seq_controle_p;

/* Gera linha do arquivo */

ds_linha_w	:= substr(	ds_sistema_w			||
				rpad(' ',4,' ' ) 		||
				to_char(dt_ano_w,'YYYY')	||
				rpad(' ',362,' ' )		|| sep_w,1,8000);


ds_arquivo_w		:= substr(ds_linha_w,1,4000);
ds_compl_arquivo_w	:= substr(ds_linha_w,4001,4000);
nr_seq_registro_w		:= nr_seq_registro_w + 1;
nr_linha_w		:= nr_linha_w + 1;

insert into fis_dipj_registro(
		nr_sequencia,
		ds_arquivo,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_controle_dipj,
		ds_arquivo_compl,
		cd_registro,
		nr_linha)
	values (
		nr_seq_registro_w,
		ds_arquivo_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_controle_p,
		ds_compl_arquivo_w,
		'HEA',
		nr_linha_w);

commit;


qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
