CREATE OR REPLACE PROCEDURE fis_gerar_reg_m205_efd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, ds_separador_p text, vl_debito_p bigint, nr_num_campo_p text, cd_tributo_pis_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE



ds_arquivo_w			varchar(4000);
ds_arquivo_compl_w		varchar(4000);
ds_linha_w			varchar(8000);
nr_seq_registro_w		bigint 	:= nr_sequencia_p;
nr_linha_w			bigint 	:= qt_linha_p;
sep_w				varchar(1) 	:= ds_separador_p;
cd_retencao_w			tributo.cd_retencao%type;
cd_variacao_w			tributo_dctf.cd_variacao%type;
cd_receita_w			varchar(6);


BEGIN
select	max(a.cd_retencao)
into STRICT	cd_retencao_w
from	tributo	a
where	a.cd_tributo	= cd_tributo_pis_p;

select	max(a.cd_variacao)
into STRICT	cd_variacao_w
from	tributo_dctf	a
where	a.cd_tributo	= cd_tributo_pis_p
and	dt_inicio_p between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia, dt_inicio_p)
and	dt_fim_p between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia, dt_fim_p);

cd_receita_w	:= substr(cd_retencao_w, 1, 4) || cd_variacao_w;

-- Montagem das linhas do arquivo Não Cumulativo
ds_linha_w	:=	sep_w || 'M205' 						|| --Campo 01 - Registro
			sep_w || nr_num_campo_p						|| --Campo 02 - Número do campo
			sep_w || cd_receita_w						 || --Campo 03- Código receita
			sep_w || replace(campo_mascara(coalesce(vl_debito_p,0),2),'.',',')	|| --Campo 04 - Valor de débito
			sep_w;

ds_arquivo_w		:= substr(ds_linha_w,1,4000);
ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
nr_seq_registro_w	:= nr_seq_registro_w + 1;
nr_linha_w		:= nr_linha_w + 1;

insert into fis_efd_arquivo(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	nr_seq_controle_efd,
	nr_linha,
	ds_arquivo,
	ds_arquivo_compl,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_registro)
values (nr_seq_registro_w,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_controle_p,
	nr_linha_w,
	ds_arquivo_w,
	ds_arquivo_compl_w,
	clock_timestamp(),
	nm_usuario_p,
	'M205');

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
