CREATE OR REPLACE PROCEDURE fis_obter_valor_trib_item ( nr_sequencia_p bigint, nr_item_nf_p bigint, ie_tipo_tributo_p text, vl_tributo_p INOUT bigint, vl_base_calc_p INOUT bigint, vl_taxa_p INOUT bigint) AS $body$
BEGIN

begin

	select	coalesce(sum(n.vl_tributo),0),
		coalesce(sum(n.vl_base_calculo),0),
		coalesce(sum(n.tx_tributo),0)
	into STRICT    vl_tributo_p,
		vl_base_calc_p,
		vl_taxa_p
	from   	tributo t,
		nota_fiscal_item_trib n
	where	t.cd_tributo		= n.cd_tributo
	and	((coalesce(ie_tipo_tributo_p,'X')	= 'X')
		or (t.ie_tipo_tributo	= coalesce(ie_tipo_tributo_p,'X')))
	and     n.nr_sequencia		= nr_sequencia_p
	and     n.nr_item_nf            = nr_item_nf_p;

exception
when others then
	vl_tributo_p	:= 0;
	vl_base_calc_p	:= 0;
	vl_taxa_p	:= 0;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
