CREATE OR REPLACE PROCEDURE fis_reg_ecf_0035 ( nr_seq_controle_p bigint, ds_separador_p text, cd_estabelecimento_p bigint, nm_usuario_p text, cd_empresa_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint, ie_scp_p text) AS $body$
DECLARE


nr_linha_w		bigint := qt_linha_p;
nr_seq_registro_w		bigint := nr_sequencia_p;
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
sep_w			varchar(1):= ds_separador_p;

c01 CURSOR FOR
	SELECT 	'0035' tp_registro,
	Elimina_Caracteres_Especiais(obter_cgc_estabelecimento(a.cd_estabelecimento)) cd_cgc,
	substr(obter_dados_pf_pj(null, a.cd_cgc, 'N'),1,100) ds_fantasia
	from estabelecimento a
	where a.ie_scp = 'S'
	and a.cd_estab_socio_ost_scp = cd_estabelecimento_p;

vet01	C01%RowType;


BEGIN

open C01;
loop
fetch C01 into	
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	
	ds_linha_w	:= substr(	sep_w || vet01.tp_registro		||
				sep_w || vet01.cd_cgc		||
				sep_w || vet01.ds_fantasia		|| sep_w, 1, 8000);
	
	ds_arquivo_w		:= substr(ds_linha_w, 1, 4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w, 4001, 4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	insert into fis_ecf_arquivo(
		nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_controle_ecf,
		nr_linha,
		cd_registro,
		ds_arquivo,
		ds_arquivo_compl)
	values (	nr_seq_registro_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_controle_p,
		nr_linha_w,
		'0035',
		ds_arquivo_w,
		ds_arquivo_compl_w);
	end;
end loop;
close C01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
