CREATE OR REPLACE PROCEDURE fis_reinf_gerar_id ( cd_estabelecimento_p bigint, ie_evento_p text, nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_id_w		varchar(50);
								


BEGIN

select ('ID' || '1' || rpad(substr(e.cd_cgc,1,8), 14, '0') || TO_CHAR(clock_timestamp(),'YYYYMMDD') || TO_CHAR(clock_timestamp(),'HHMMSS')  ||'00001')
into STRICT	nr_id_w
from	estabelecimento e
where	cd_estabelecimento = cd_estabelecimento_p;


if (ie_evento_p = '4010') then
	begin
		update fis_reinf_r4010_evento set cd_id = nr_id_w where ie_selecionado = 'S' and nr_seq_lote = nr_seq_lote_p;
	end;
elsif (ie_evento_p = '4020') then
	begin
		update fis_reinf_r4020_evento set cd_id = nr_id_w where ie_selecionado = 'S' and nr_seq_lote = nr_seq_lote_p;
	end;
elsif (ie_evento_p = '4040') then
	begin
		update fis_reinf_r4040_evento set cd_id = nr_id_w where ie_selecionado = 'S' and nr_seq_lote = nr_seq_lote_p;
	end;
elsif (ie_evento_p = '4098') then
	begin
		update fis_reinf_r4098 set cd_id = nr_id_w where nr_sequencia = nr_seq_lote_p;
	end;
end if;

end;
$body$
