CREATE OR REPLACE PROCEDURE fleury_limpa_validacao_item ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) AS $body$
BEGIN

delete
from 	fleury_prescr_proc_mat
where	nr_prescricao 	= nr_prescricao_p
and	nr_seq_prescr	= coalesce(nr_seq_prescr_p,nr_seq_prescr);

delete
from	fleury_prescr_proced_valid
where	nr_prescricao 	= nr_prescricao_p
and	nr_seq_prescr	= coalesce(nr_seq_prescr_p,nr_seq_prescr);

delete
from	prescr_medica_erro
where	nr_prescricao 	= nr_prescricao_p
and	nr_seq_proced 	= coalesce(nr_seq_prescr_p,nr_seq_proced);

end;
