SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fleury_remover_item_conting ( cd_estabelecimento_p bigint, nr_controle_p text) AS $body$
DECLARE

 
ie_agrupa_w		lab_parametro.ie_agrupa_ficha_fleury%type;
nr_prescricao_w		prescr_medica.nr_prescricao%type;
					

BEGIN 
 
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') or (nr_controle_p IS NOT NULL AND nr_controle_p::text <> '') then 
	 
	select	coalesce(max(ie_agrupa_ficha_fleury),'N') 
	into STRICT	ie_agrupa_w 
	from	lab_parametro 
	where	cd_estabelecimento = cd_estabelecimento_p;
	 
	if (ie_agrupa_w <> 'N') then 
		select	max(nr_prescricao) 
		into STRICT	nr_prescricao_w 
		from	prescr_procedimento 
		where	nr_controle_ext = nr_controle_p;
	else 
		select	max(nr_prescricao) 
		into STRICT	nr_prescricao_w 
		from 	prescr_medica 
		where	nr_controle 	= nr_controle_p;
		 
		if (coalesce(nr_prescricao_w::text, '') = '') then 
			select	max(b.nr_prescricao) 
			into STRICT	nr_prescricao_w 
			from	prescr_procedimento a, prescr_medica b 
			where	a.nr_prescricao = b.nr_prescricao 
			and	a.nr_controle_ext = nr_controle_p 
			and	((b.cd_estabelecimento = cd_estabelecimento_p) or (cd_estabelecimento_p = 0));
		end if;
		 
	end if;	
 
	if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then 
		CALL fleury_remover_dt_integracao(nr_prescricao_w);
	end if;
	 
	delete	FROM FICHA_CONTINGENCIA_FLEURY 
	where	nr_controle = nr_controle_p 
	and	cd_estabelecimento = cd_estabelecimento_p;
	 
	commit;
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
