SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fleury_ws_susp_item_prescr ( nr_ficha_p text, cd_unidade_p text, nr_seq_prescr_p bigint) AS $body$
DECLARE


cd_estabelecimento_w	bigint;
ie_agrupa_w				varchar(1);
nr_prescricao_w			bigint := null;
nr_seq_prescr_w			prescr_procedimento.nr_sequencia%type;
--nr_seq_prescr_w			varchar2(255);
ie_parametro_91_w		varchar(1);
ds_retorno_w			varchar(4000);
					

BEGIN

if coalesce(wheb_usuario_pck.get_nm_usuario::text, '') = '' then
   CALL wheb_usuario_pck.set_nm_usuario('WebService');
end if;

select	fleury_obter_dados_unidade(cd_unidade_p, 'E'),
		fleury_obter_dados_unidade(cd_unidade_p, 'AF')
into STRICT	cd_estabelecimento_w,
		ie_agrupa_w
;

if (ie_agrupa_w = 'S') then
	select	max(a.nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_procedimento a,
			prescr_medica b
	where	a.nr_prescricao = b.nr_prescricao
	and		a.nr_controle_ext = nr_ficha_p
	and		b.cd_estabelecimento = cd_estabelecimento_w;
else
	select	max(a.nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_medica a
	where	a.nr_controle = nr_ficha_p
	and		a.cd_estabelecimento = cd_estabelecimento_w;
	
	/*if (nr_prescricao_w is null) then
		select	max(b.nr_prescricao)
		into	nr_prescricao_w
		from	prescr_procedimento a, prescr_medica b
		where	a.nr_prescricao = b.nr_prescricao
		and	a.nr_controle_ext = nr_ficha_p
		and	((b.cd_estabelecimento = cd_estabelecimento_w) or (cd_estabelecimento_w = 0));
	end if;*/
	
end if;

--nr_seq_prescr_w := obter_seq_prescr_prescricao(nr_prescricao_w, 'FLEURYWS', cd_exame_p);
if (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then
	nr_seq_prescr_w	:= nr_seq_prescr_p;
end if;
	
if (coalesce(nr_seq_prescr_w::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(194701, obter_desc_expressao(504171)/*'SEQUENCIA='*/
||coalesce(nr_seq_prescr_w,'Valor nao encontrado'));		
end if;

ie_parametro_91_w	:= coalesce(obter_valor_param_usuario(924, 91, null, 'FLEURYWS', cd_estabelecimento_w),'N');

ds_retorno_w := Consiste_supensao_prescr(nr_prescricao_w, nr_seq_prescr_w, 'PRESCR_PROCEDIMENTO', ie_parametro_91_w, cd_estabelecimento_w, ds_retorno_w, 'FLEURYWS', 924);
CALL Suspender_item_Prescricao(nr_prescricao_w, nr_seq_prescr_w, 0, null, 'PRESCR_PROCEDIMENTO', 'FLEURYWS');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
