CREATE OR REPLACE PROCEDURE forcar_horario_livre_agenda (cd_agenda_p bigint, dt_agenda_p timestamp, hr_inicio_p timestamp, nr_minuto_duracao_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		bigint;

cd_estabelecimento_w	smallint;
cd_medico_w		varchar(10);
nr_seq_sala_w		bigint;


BEGIN 
select	nextval('agenda_livre_forcado_seq') 
into STRICT	nr_sequencia_w
;
 
/* obter dados agenda (Rafael em 27/02/2007 OS51271) */
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	agenda 
where	cd_agenda = cd_agenda_p;
 
select	substr(obter_dados_cad_hor_agenda(cd_estabelecimento_w,cd_agenda_p, dt_agenda_p,'M'),1,10), 
	substr(obter_dados_cad_hor_agenda(cd_estabelecimento_w,cd_agenda_p, dt_agenda_p,'S'),1,10) 
into STRICT	cd_medico_w, 
	nr_seq_sala_w
;
 
 
insert	into agenda_livre_forcado(NR_SEQUENCIA, 
	DT_ATUALIZACAO, 
	NM_USUARIO, 
	CD_AGENDA, 
	DT_AGENDA, 
	NR_MINUTO_DURACAO) 
values (nr_sequencia_w, 
	clock_timestamp(), 
	nm_usuario_p, 
	cd_agenda_p, 
	hr_inicio_p, 
	nr_minuto_duracao_p);
 
select	nextval('agenda_paciente_seq') 
into STRICT	nr_sequencia_w
;
 
insert	into agenda_paciente(cd_agenda, 
	dt_agenda, 
	hr_inicio, 
	nr_minuto_duracao, 
	nm_usuario, 
	dt_atualizacao, 
	nr_sequencia, 
	ie_equipamento, 
	ie_status_agenda, 
	nm_paciente, 
	cd_medico_exec, 
	nr_seq_sala) 
values (cd_agenda_p, 
	dt_agenda_p, 
	hr_inicio_p, 
	nr_minuto_duracao_p, 
	nm_usuario_p, 
	clock_timestamp(), 
	nr_sequencia_w, 
	'N', 'LF', ds_observacao_p, 
	cd_medico_w, 
	nr_seq_sala_w);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
