CREATE OR REPLACE PROCEDURE formatar_primeiro_horario ( ds_horario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_tamanho_w	bigint;
ds_horario_w	varchar(225);
qt_temp_w	varchar(5);
dt_prim_w	timestamp;


BEGIN

ds_horario_w	:= ds_horario_p;
if (length(trim(both ds_horario_p)) = 3) then
	ds_horario_w	:= substr(trim(both ds_horario_p),1,length(trim(both ds_horario_p))-1);
end if;

ds_retorno_p	:= 'S';
qt_tamanho_w	:= Length(ds_horario_w);
qt_temp_w	:= ds_horario_w;
begin
if (position(' ' in ds_horario_w) <> 0) then
	ds_retorno_p	:= 'N';
elsif (qt_tamanho_w = 2) and
	(((qt_temp_w)::numeric  > 24) or (position(':' in ds_horario_w) <> 0)) then
	ds_retorno_p	:= 'N';
elsif (qt_tamanho_w = 3) then
	ds_retorno_p	:= 'N';
elsif (qt_tamanho_w = 4) then
	ds_retorno_p	:= 'N';
elsif (qt_tamanho_w = 5) and (position(':' in ds_horario_w) = 0) then
	ds_retorno_p	:= 'N';
end if;

if (ds_retorno_p <> 'N') then
	begin
	if (qt_tamanho_w in (1,2)) then
	   	begin
	   	if (qt_tamanho_w = 1) then
	      		qt_temp_w := '0'||ds_horario_w||':00';
		else
			qt_temp_w := ds_horario_w||':00';
		end if;
	   	if (substr(qt_temp_w,1,2) = '24') then
			qt_temp_w := '00:00';
	   	end if;
		end;
	end if;
	if (qt_temp_w IS NOT NULL AND qt_temp_w::text <> '') and ((substr(qt_temp_w,1,2))::numeric  > 23) then
		ds_retorno_p	:= 'N';
	else
		ds_retorno_p	:= qt_temp_w;
	end if;
	end;
end if;
exception when others then
	ds_retorno_p	:= 'N';
end;

END;
$body$
