CREATE OR REPLACE PROCEDURE fu_incluir_item_adicional ( nr_sequencia_p bigint, nr_seq_diluicao_p bigint, qt_porcentagem_p bigint, qt_dose_adic_p bigint, ie_tipo_p text) AS $body$
DECLARE


cd_material_adic_w			nutricao_produto.cd_material%type;
cd_unidade_medida_adic_w	nutricao_produto.cd_unidade_medida%type;
nr_seq_produto_w			nutricao_produto.nr_sequencia%type;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select 	cd_material,
			nr_sequencia,
			cd_unidade_medida
	into STRICT	cd_material_adic_w,
			nr_seq_produto_w,
			cd_unidade_medida_adic_w
	from	nutricao_produto
	where	nr_sequencia = nr_sequencia_p;

end if;

insert into nut_atend_serv_dia_dieta(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					cd_material,
					ie_tipo_nutricao,
					nr_sequencia_diluicao,
					hr_prim_horario,
					qt_dose,
					qt_porcentagem,
					cd_unidade_medida_dosa,
					nr_seq_produto
				) values (
					nextval('nut_atend_serv_dia_dieta_seq'),
					clock_timestamp(),
					wheb_usuario_pck.get_nm_usuario,
					clock_timestamp(),
					wheb_usuario_pck.get_nm_usuario,
					cd_material_adic_w,
					ie_tipo_p,
					nr_seq_diluicao_p,
					'00:00',
					CASE WHEN qt_dose_adic_p=0 THEN null  ELSE qt_dose_adic_p END ,
					CASE WHEN qt_porcentagem_p=0 THEN null  ELSE qt_porcentagem_p END ,
					cd_unidade_medida_adic_w,
					nr_seq_produto_w);

commit;

end;
$body$
