CREATE OR REPLACE PROCEDURE gap_desvincular_os_requisito ( nr_seq_requisito_p LATAM_REQUISITO.NR_SEQ_REQUISITO%TYPE, nr_seq_ordem_serv_p LATAM_REQUISITO.NR_SEQ_ORDEM_SERV%TYPE, nm_usuario_p LATAM_REQUISITO.NM_USUARIO%TYPE ) AS $body$
DECLARE


dt_atualizacao_s            CONSTANT LATAM_REQUISITO_OS.DT_ATUALIZACAO%TYPE := clock_timestamp();
nr_seq_ordem_serv_w         LATAM_REQUISITO_OS.NR_SEQ_ORDEM_SERV%TYPE;
ie_liberar_desenv_old_p     LATAM_REQUISITO.IE_LIBERAR_DESENV%TYPE;


BEGIN
	SELECT 	MAX(NR_SEQ_ORDEM_SERV)
	INTO STRICT   	nr_seq_ordem_serv_w
	FROM   	LATAM_REQUISITO_OS
	WHERE  	NR_SEQ_REQUISITO 	=   nr_seq_requisito_p
	AND 	NR_SEQUENCIA 		<>  nr_seq_ordem_serv_p;

	UPDATE 		LATAM_REQUISITO
	SET 		NR_SEQ_ORDEM_SERV	 	= nr_seq_ordem_serv_w,
                      DT_ATUALIZACAO_NREC		= dt_atualizacao_s,
                      NM_USUARIO_NREC 		= nm_usuario_p
	WHERE  		NR_SEQUENCIA 			= nr_seq_requisito_p
	RETURNING 	IE_LIBERAR_DESENV INTO ie_liberar_desenv_old_p;

	DELETE FROM LATAM_REQUISITO_OS
	WHERE  NR_SEQUENCIA = nr_seq_ordem_serv_p;

	CALL GENERATE_LATAM_LOG(nr_seq_requisito_p, ie_liberar_desenv_old_p, 323167);

COMMIT;
END;
$body$
