CREATE OR REPLACE PROCEDURE gap_liberar_requisitos (nr_seq_requisito_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE
	

    ie_liberar_desenv_old_w latam_requisito.ie_liberar_desenv%type;

BEGIN

IF (ie_opcao_p = 'A') THEN	
    UPDATE	    LATAM_REQUISITO
    SET         DT_LIBERACAO	    = clock_timestamp(),
                NM_USUARIO_LIB	= nm_usuario_p
    WHERE       NR_SEQUENCIA 	    = nr_seq_requisito_p
    RETURNING   IE_LIBERAR_DESENV INTO ie_liberar_desenv_old_w;
ELSE
    UPDATE      LATAM_REQUISITO
    SET         DT_LIBERACAO	 = NULL,
                NM_USUARIO_LIB	 = NULL
    WHERE       NR_SEQUENCIA  = nr_seq_requisito_p
    RETURNING   IE_LIBERAR_DESENV INTO ie_liberar_desenv_old_w;
END IF;

CALL generate_latam_log(nr_seq_requisito_p, ie_liberar_desenv_old_w, CASE WHEN ie_opcao_p = 'A' THEN 717011 ELSE 317804 END);

COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
