CREATE OR REPLACE PROCEDURE gc_atualiza_entrega_kit_js ( nr_entregar_kit_p bigint, nr_entregar_doc_interno_p text, nr_entregar_doc_interno_aux_p text, nr_prescricao_p bigint, ie_opcao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_tipo_ident_p text, ie_tipo_ident_aux_p text ) AS $body$
DECLARE



ie_se_cod_interno_valido_w	varchar(1);
consiste_doc_interno_valido_w	varchar(1);
ds_retorno_w			varchar(255);
qt_prescr_material_w		bigint;
nr_entregar_kit_w		integer;
nr_seq_kit_estoque_w		bigint;

BEGIN

consiste_doc_interno_valido_w := obter_param_usuario(900, 173, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, consiste_doc_interno_valido_w);

if (consiste_doc_interno_valido_w = 'S') then
	begin
	ie_se_cod_interno_valido_w := obter_se_cod_interno_valido(nr_entregar_doc_interno_p,ie_tipo_ident_p);

	if (ie_se_cod_interno_valido_w = 'N') then
		begin
		--Alterado de 51815 para 224804
		CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(224804);
		end;
	end if;

	ie_se_cod_interno_valido_w := obter_se_cod_interno_valido(nr_entregar_doc_interno_aux_p,ie_tipo_ident_aux_p);

	if (ie_se_cod_interno_valido_w = 'N') then
		begin
		CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(193008);
		end;
	end if;
	end;
end if;


if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_prescr_material_w
	from   	prescr_material
	where	nr_prescricao   = nr_prescricao_p
	and	cd_kit_material = nr_entregar_kit_p;
	end;
end if;


if (ie_opcao_p <> 0) and (qt_prescr_material_w = 0) then
	begin
	CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(51817);
	end;
end if;

if (ie_opcao_p <> 1) then
	begin

	if (ie_opcao_p = 2) then
		begin
		nr_entregar_kit_w := nr_entregar_kit_p;
		end;
	else
		begin
		nr_seq_kit_estoque_w := nr_entregar_kit_p;
		end;
	end if;

	ds_retorno_w := consistir_entrega_kit(nr_entregar_kit_w, nr_seq_kit_estoque_w, nr_entregar_doc_interno_p, nr_entregar_doc_interno_aux_p, ie_tipo_ident_p, ie_tipo_ident_aux_p, nm_usuario_p, ds_retorno_w);

	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
		begin
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(224820,'DS_MENSAGEM_P=' ||ds_retorno_w);
		end;
	end if;
	end;
end if;


nr_entregar_kit_w 	:= null;
nr_seq_kit_estoque_w 	:= null;

if (ie_opcao_p = 1) or (ie_opcao_p = 2)then
	begin
	nr_entregar_kit_w := nr_entregar_kit_p;
	end;
else
	begin
	nr_seq_kit_estoque_w := nr_entregar_kit_p;
	end;
end if;

CALL entregar_kit_cirurgia(nr_prescricao_p,nr_entregar_kit_w,nr_seq_kit_estoque_w,nr_entregar_doc_interno_p,nr_entregar_doc_interno_aux_p,ie_tipo_ident_p,ie_tipo_ident_aux_p,nm_usuario_p);
commit;

end;
$body$
