CREATE OR REPLACE PROCEDURE gc_excl_itens_nao_consist_js ( nr_prescricao_p bigint, nr_prescricao_cirurgia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_mensagem_p INOUT text, nr_requisicao_p INOUT bigint) AS $body$
DECLARE

 
qt_prescr_material_w		bigint;
exc_itens_nconsist_final_w	varchar(1);
ds_mensagem_w			varchar(255) := '';
nr_sequisicao_w			bigint;


BEGIN 
 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
	begin 
	select	count(*) 
	into STRICT 	qt_prescr_material_w 
	from 	prescr_material 
	where 	nr_prescricao    = nr_prescricao_p 
	and 	ie_status_cirurgia  = 'CB';
	 
	exc_itens_nconsist_final_w := obter_param_usuario(900, 228, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, exc_itens_nconsist_final_w);
	 
	 
	if (exc_itens_nconsist_final_w = 'S') and (qt_prescr_material_w > 0) then 
		begin 
			SELECT * FROM gc_exc_itens_nao_cons_final_js(nr_prescricao_cirurgia_p, nr_prescricao_p, nm_usuario_p, cd_estabelecimento_p, ds_mensagem_w, nr_sequisicao_w) INTO STRICT ds_mensagem_w, nr_sequisicao_w;
		end;
	end if;
	 
	end;
end if;
 
ds_mensagem_p := ds_mensagem_w;
nr_requisicao_p  := nr_sequisicao_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
