--end if; 
 
 
if (coalesce(nr_requisicao_w,0) > 0) then 
	begin 
	 
	ds_mensagem_w	:= substr(obter_texto_dic_objeto(71147,wheb_usuario_pck.get_nr_seq_idioma,'NR_REQUISICAO='|| nr_requisicao_w),1,255);
	 
	end;
	 
	 
