CREATE OR REPLACE PROCEDURE gc_gerar_encaixe_js ( nr_cirurgia_p bigint, cd_medico_enc_p text, qt_min_dur_p bigint, cd_pessoa_enc_p text, ds_pessoa_enc_p text, nr_seq_proc_interno_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_lado_p text, cd_convenio_p bigint, obs_enc_p text, ie_carater_cirurgia_p text, nr_tefefone_p text, qt_idade_p bigint, nm_pessoa_contato_enc_p text, cd_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_agenda_p INOUT bigint, cd_procedencia_p bigint default null) AS $body$
DECLARE


ie_termino_cirurgia_pac_w	varchar(254);
ie_status_cirur_paciente_w	varchar(3);
ie_permite_gerar_encaixe_w	varchar(1);
nr_seq_agenda_w		bigint;
ds_erro_2_w		varchar(255);
				

BEGIN

if (coalesce(cd_pessoa_enc_p::text, '') = '') and (coalesce(ds_pessoa_enc_p::text, '') = '') then
	begin
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(53521);
	end;
end if;

select	coalesce(max('S'),'N')
into STRICT	ie_termino_cirurgia_pac_w
from	cirurgia
where	cd_pessoa_fisica = cd_pessoa_enc_p
and	(dt_inicio_real IS NOT NULL AND dt_inicio_real::text <> '')
and	dt_termino is  null
and      	ie_status_cirurgia not in (3,4);

ie_permite_gerar_encaixe_w := obter_param_usuario(900, 157, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_gerar_encaixe_w);

if (ie_permite_gerar_encaixe_w = 'N') then
	begin
	if ( ie_termino_cirurgia_pac_w = 'S')  then
		begin 
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(53523);
		end;
	end if;
	end;
end if;

SELECT * FROM gerar_encaixe_gestao_agenda(	dt_agenda_p, qt_min_dur_p, cd_pessoa_enc_p, ds_pessoa_enc_p, cd_medico_enc_p, nr_seq_proc_interno_p, cd_procedimento_p, ie_origem_proced_p, ie_lado_p, cd_convenio_p, cd_agenda_p, cd_estabelecimento_p, nm_usuario_p, obs_enc_p, ie_carater_cirurgia_p, nr_tefefone_p, qt_idade_p, 0, nm_pessoa_contato_enc_p, null, nr_seq_agenda_w, cd_procedencia_p, null, null, ds_erro_2_w, null) INTO STRICT nr_seq_agenda_w, ds_erro_2_w;

commit;

nr_seq_agenda_p := nr_seq_agenda_w;

end;
$body$
