CREATE OR REPLACE PROCEDURE gc_gerar_presc_cirurgia_js ( cd_local_estoque_p bigint, nr_seq_kit_estoque_p bigint, nr_cirurgia_p bigint, ie_origem_inf_p text, nr_prescricao_p bigint, nr_doc_interno_p bigint, nr_doc_interno_aux_p bigint, cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
qt_kit_estoque_w		bigint;
consistir_itens_kit_est_w	varchar(1);
					

BEGIN 
 
if (nr_seq_kit_estoque_p IS NOT NULL AND nr_seq_kit_estoque_p::text <> '') then 
	begin 
	 
	consistir_itens_kit_est_w := obter_param_usuario(900, 153, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, consistir_itens_kit_est_w);
	 
	select	count(*) 
	into STRICT	qt_kit_estoque_w 
	from	kit_estoque_comp 
	where	nr_seq_kit_estoque = nr_seq_kit_estoque_p;
	 
	if (consistir_itens_kit_est_w = 'S') and (qt_kit_estoque_w = 0) then 
		begin 
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(50892);
		end;
	end if;
	 
	CALL gerar_prescricao_cirurgia(0,nr_seq_kit_estoque_p,nr_cirurgia_p,0,ie_origem_inf_p,nm_usuario_p,nr_prescricao_p,nr_doc_interno_p,cd_local_estoque_p,900,cd_perfil_p,'',nr_doc_interno_aux_p,null,null);
	 
	end;
end if;
 
end;
