CREATE OR REPLACE PROCEDURE gc_gerar_todas_cir_prescr_js ( nr_seq_agenda_p bigint, cd_medico_p text, cd_pessoa_fisica_p text, cd_procedimento_p bigint, nr_cirurgia_p bigint, nr_doc_interno_p bigint, nr_doc_interno_aux_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_origem_inf_p text, cd_setor_atendimento_p bigint, nr_atendimento_p bigint ) AS $body$
DECLARE

					 
nr_prescricao_w		bigint;
qt_prescr_medica_w	bigint;
ie_gera_prescr_sem_atend_w varchar(1);
ie_gerar_prescricao_cirurgia_w varchar(10);/*opcoes s√£o apenas "S" e "N"*/
			 

BEGIN 
 
ie_gera_prescr_sem_atend_w := Obter_Param_Usuario(900, 245, wheb_usuario_pck.get_cd_perfil, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_gera_prescr_sem_atend_w);
ie_gerar_prescricao_cirurgia_w := Obter_Param_Usuario(900, 389, wheb_usuario_pck.get_cd_perfil, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_gerar_prescricao_cirurgia_w);
 
 
if	((cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') and 
	((coalesce(nr_atendimento_p,0) > 0) or (ie_gera_prescr_sem_atend_w = 'S'))) then 
	begin 
	 
	 
	 
	if (coalesce(nr_cirurgia_p::text, '') = '' or nr_cirurgia_p = 0) then 
		begin 
		CALL gerar_cirurgia_agenda(cd_estabelecimento_p,nr_seq_agenda_p,nm_usuario_p,cd_setor_atendimento_p);
		end;
	end if;
	 
	select 	count(*) 
	into STRICT	qt_prescr_medica_w 
	from	prescr_medica a, 
		prescr_material b 
	where	a.nr_prescricao = b.nr_prescricao 
	and	nr_seq_agenda = nr_seq_agenda_p;
	 
	end;
	 
	if (qt_prescr_medica_w = 0) then 
		begin 
		 
		nr_prescricao_w := gerar_prescricao_agenda(nr_seq_agenda_p, nm_usuario_p, nr_prescricao_w, cd_estabelecimento_p);
		 
		CALL gerar_proc_agenda_cirurgia(nr_seq_agenda_p,nr_prescricao_w,nm_usuario_p);
		if (ie_gerar_prescricao_cirurgia_w = 'S') then 
			CALL gerar_prescricao_cirurgia(0,0,0,nr_seq_agenda_p,ie_origem_inf_p,nm_usuario_p,nr_prescricao_w,nr_doc_interno_p,null,900,obter_perfil_ativo,null,nr_doc_interno_aux_p,null,null);	
		end if;
		end;
	end if;
	 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
