SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gc_obter_dados_local_pac_js ( nr_prescricao_p bigint, nr_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_cirurgia_p INOUT bigint, cd_pessoa_fisica_p INOUT text, ds_mensagem_geral_p INOUT text, ds_mensagem_atend_dif_p INOUT text, ds_pergunta_p INOUT text ) AS $body$
DECLARE

 
ie_local_cons_cancel_inte_w	varchar(1);
nr_cirurgia_w		bigint	:= 0;
cd_pessoa_fisica_w	varchar(10) 	:= '';
ds_mensagem_geral_w	varchar(255)	:= '';
ds_mensagem_atend_dif_w	varchar(255)	:= '';
ds_pergunta_w		varchar(255)	:= '';
				

BEGIN 
 
ie_local_cons_cancel_inte_w := obter_param_usuario(900, 181, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_local_cons_cancel_inte_w);
 
if (nr_prescricao_p > 0) then 
	begin 
	 
	select	max(nr_cirurgia) 
	into STRICT	nr_cirurgia_w 
	from	cirurgia 
	where	nr_prescricao = nr_prescricao_p 
	and	((ie_local_cons_cancel_inte_w = 'S') 
	or	(ie_local_cons_cancel_inte_w = 'N' AND ie_status_cirurgia <> 3 and ie_status_cirurgia <> 4));
	 
	end;
end if;
 
if (nr_atendimento_p > 0) then 
	begin 
	 
	select max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from  atendimento_paciente 
	where  nr_atendimento = nr_atendimento_p;						
	 
	end;
end if;
--Não há agendamentos para esta paciente! 
ds_mensagem_geral_w	:= substr(obter_texto_tasy(54534, wheb_usuario_pck.get_nr_seq_idioma),1,255);
 
ds_mensagem_atend_dif_w	:= substr(obter_texto_dic_objeto(54537,wheb_usuario_pck.get_nr_seq_idioma,'nr_atendimento='|| nr_atendimento_p),1,255);
			  
ds_pergunta_w		:= substr(obter_texto_dic_objeto(54539,wheb_usuario_pck.get_nr_seq_idioma,'nr_atendimento='|| nr_atendimento_p),1,255);
 
nr_cirurgia_p		:= nr_cirurgia_w;
cd_pessoa_fisica_p	:= cd_pessoa_fisica_w;
ds_mensagem_geral_p	:= ds_mensagem_geral_w;
ds_mensagem_atend_dif_p	:= ds_mensagem_atend_dif_w;
ds_pergunta_p		:= ds_pergunta_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
