CREATE OR REPLACE PROCEDURE gc_realizar_proced_previsto_js ( nr_cirurgia_p bigint, nr_prescricao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_local_estoque_p bigint default null, ie_unid_setor_cirurgia_p INOUT text DEFAULT NULL, ds_mensagem_p INOUT text  DEFAULT NULL) AS $body$
DECLARE


ie_setar_via_maior_custo_w 	varchar(1);
ie_gerar_particip_proc_w	varchar(1);
ie_msg_relizar_procedimentos_w	varchar(255);
ie_unid_setor_cirurgia_w	varchar(255);
ds_mensagem_w			varchar(255);
		

BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	begin
	
	ie_setar_via_maior_custo_w := obter_param_usuario(900, 168, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_setar_via_maior_custo_w);
	ie_gerar_particip_proc_w := obter_param_usuario(900, 215, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_gerar_particip_proc_w);
	ie_msg_relizar_procedimentos_w := obter_param_usuario(900, 212, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_msg_relizar_procedimentos_w);
	
	CALL realizar_proc_previstos(nr_cirurgia_p,nr_prescricao_p,nm_usuario_p, 'S');

	CALL executar_taxa_equipamento(nr_cirurgia_p,nm_usuario_p);

	CALL executar_material_equipamento(nr_cirurgia_p, cd_local_estoque_p, nm_usuario_p);
	
	ie_unid_setor_cirurgia_w := obter_unid_setor_cirurgia(nr_cirurgia_p,'C');

        if (ie_setar_via_maior_custo_w = 'S') then
		begin
		CALL seta_via_princ_proc_maior_vl(nr_cirurgia_p);
		end;
	end if;
	

        if (ie_gerar_particip_proc_w = 'S') then
		begin
		CALL gerar_particip_proc(nr_cirurgia_p,nm_usuario_p);
		end;
	end if;
	
	if (ie_msg_relizar_procedimentos_w IS NOT NULL AND ie_msg_relizar_procedimentos_w::text <> '') then
		begin
		ds_mensagem_w := substr(obter_texto_tasy(54367, wheb_usuario_pck.get_nr_seq_idioma),1,255) || ie_msg_relizar_procedimentos_w;
		end;
         else
		begin
		ds_mensagem_w := substr(obter_texto_tasy(54367, wheb_usuario_pck.get_nr_seq_idioma),1,255);
		end;
	end if;
	end;
end if;

ie_unid_setor_cirurgia_p	:= ie_unid_setor_cirurgia_w;
ds_mensagem_p			:= ds_mensagem_w;

end;
$body$
