CREATE OR REPLACE PROCEDURE gc_reg_fim_conf_gastos_js ( dt_fim_conferencia_p timestamp, nr_cirurgia_p bigint ) AS $body$
BEGIN

if (dt_fim_conferencia_p IS NOT NULL AND dt_fim_conferencia_p::text <> '') then
	begin

	update	cirurgia
	set	dt_fim_conferencia  = NULL,
		nm_usuario_fim_conferencia  = NULL
	where	nr_cirurgia = nr_cirurgia_p
	and (dt_fim_conferencia IS NOT NULL AND dt_fim_conferencia::text <> '');
	end;
else
	begin

	update 	cirurgia
	set 	dt_fim_conferencia = clock_timestamp(),
		nm_usuario_fim_conferencia = wheb_usuario_pck.get_nm_usuario
	where 	nr_cirurgia = nr_cirurgia_p
	and 	coalesce(dt_fim_conferencia::text, '') = '';

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
