CREATE OR REPLACE PROCEDURE gc_valida_gerar_prescr_cir_js ( nr_seq_agenda_p bigint, qt_agenda_paciente_p INOUT bigint, qt_prescr_medica_p INOUT bigint ) AS $body$
DECLARE


qt_agenda_paciente_w 	bigint;
qt_prescr_medica_w 	bigint;


BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_agenda_paciente_w
	from	agenda_paciente a
	where	nr_sequencia = nr_seq_agenda_p
	and	(nr_cirurgia IS NOT NULL AND nr_cirurgia::text <> '');

	select	count(*)
	into STRICT	qt_prescr_medica_w
	from	prescr_medica a,
		prescr_material b
	where	a.nr_prescricao = b.nr_prescricao
	and	nr_seq_agenda = nr_seq_agenda_p;
	end;
end if;

qt_agenda_paciente_p	:= qt_agenda_paciente_w;
qt_prescr_medica_p	:= qt_prescr_medica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
