CREATE OR REPLACE PROCEDURE gedipa_atualizar_status_etapa (nr_seq_etapa_gedipa_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

qtd_etapa_hor_pendente_w bigint;
cd_material_w bigint;
qt_consumo_w double precision;
cd_unidade_medida_w varchar(30);
nr_seq_lote_fornec_w bigint;
cd_local_estoque_w smallint := null;


BEGIN

select 	count(*)
into STRICT 	qtd_etapa_hor_pendente_w
from 	gedipa_etapa_hor a, prescr_mat_hor c
where 	a.ie_status_preparo <> 'R'
and 	c.nr_sequencia = a.nr_seq_horario
and 	coalesce(c.nr_seq_superior::text, '') = ''
and 	a.nr_seq_etapa_gedipa = nr_seq_etapa_gedipa_p;

if (qtd_etapa_hor_pendente_w = 0) then
	begin

		update 	gedipa_etapa_producao
		set 	ie_status_etapa = 'P'
		where 	nr_sequencia = nr_seq_etapa_gedipa_p;

		commit;

	end;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
