CREATE OR REPLACE PROCEDURE gedipa_desvinc_hor_etapa ( nr_seq_gedipa_etapa_hor bigint, nr_seq_prescr_mat_hor bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE
											
nr_seq_etapa_gedipa_w bigint := 0;

BEGIN

select 	max(nr_seq_etapa_gedipa)
into STRICT 	nr_seq_etapa_gedipa_w
from 	gedipa_etapa_hor
where 	nr_sequencia = nr_seq_gedipa_etapa_hor;

update 	prescr_mat_hor
set 	nr_seq_etapa_gedipa  = NULL
where 	nr_sequencia = nr_seq_prescr_mat_hor;

delete	
from	gedipa_etapa_prod_disp a
where	a.nr_seq_gedipa_hor	= nr_seq_gedipa_etapa_hor;

delete
from 	w_gedipa_preparo
where 	nr_seq_etapa_hor = nr_seq_gedipa_etapa_hor;

delete
from 	gedipa_etapa_hor
where 	nr_sequencia = nr_seq_gedipa_etapa_hor;

commit;

CALL gedipa_atualizar_status_etapa(nr_seq_etapa_gedipa_w, cd_estabelecimento_p, nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
