CREATE OR REPLACE PROCEDURE gedipa_estorna_presc_mat ( nr_seq_processo_p bigint, nr_seq_etiqueta_p bigint, nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w			bigint;
nr_atendimento_w		bigint;
ie_fim_conta_w			varchar(1);
dt_fim_conta_w			timestamp;
cd_setor_atendimento_w 	integer;
nr_seq_mat_hor_w 	  bigint;

C01 CURSOR FOR 
  SELECT coalesce(a.nr_sequencia,0) 
  from	material_atend_paciente a 
  where	a.nr_seq_processo  =  nr_seq_processo_p 
  and   a.cd_material    in (SELECT x.cd_material 
                  from  prescr_mat_hor x 
                  where  x.nr_seq_etiqueta   =  nr_seq_etiqueta_p 
                  and   x.nr_prescricao    =  nr_prescricao_p);


BEGIN 
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (nr_seq_etiqueta_p IS NOT NULL AND nr_seq_etiqueta_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
   
  select	max(coalesce(a.nr_atendimento,0)) 
  into STRICT  nr_atendimento_w 
  from	material_atend_paciente a 
  where	a.nr_seq_processo =  nr_seq_processo_p 
  and   a.cd_material in (SELECT x.cd_material 
                 from  prescr_mat_hor x 
                 where  x.nr_seq_etiqueta   =  nr_seq_etiqueta_p 
                 and   x.nr_prescricao    =  nr_prescricao_p);
   
  if (nr_atendimento_w > 0) then 
    select	ie_fim_conta, 
        dt_fim_conta 
    into STRICT	ie_fim_conta_w, 
        dt_fim_conta_w 
    from	atendimento_paciente 
    where	nr_atendimento = nr_atendimento_w;
     
    if (ie_fim_conta_w = 'F') and (dt_fim_conta_w IS NOT NULL AND dt_fim_conta_w::text <> '') then 
      CALL wheb_mensagem_pck.exibir_mensagem_abort(233434);
    end if;
  end if;
 
  open C01;
  loop 
  fetch C01 into	 
    nr_sequencia_w;
  EXIT WHEN NOT FOUND; /* apply on C01 */
    begin     
   
    CALL voltar_item_pendente_prescr(nr_sequencia_w, nm_usuario_p);
       
    CALL excluir_glosa_valor_mat(nr_sequencia_w, nm_usuario_p);
    end;
  end loop;
  close C01;
   
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
