CREATE OR REPLACE PROCEDURE gedipa_estorn_bai_pres_mat ( nr_seq_etiqueta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w			bigint;
nr_atendimento_w		bigint;
ie_fim_conta_w			varchar(1);
dt_fim_conta_w			timestamp;
cd_setor_atendimento_w 		integer;
nr_seq_etapa_prod_disp_w 	bigint;

c01 CURSOR FOR 
	SELECT 	c.nr_sequencia 	 
	FROM 	prescr_mat_hor a, 
		gedipa_etapa_hor b, 
		gedipa_etapa_prod_disp c 
	WHERE  a.nr_seq_etiqueta = nr_seq_etiqueta_p 
	AND 	a.nr_sequencia = b.nr_seq_horario 
	AND 	b.nr_sequencia = c.nr_seq_gedipa_hor;
					

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_etapa_prod_disp_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	select	coalesce(max(nr_sequencia),0), 
		coalesce(max(nr_atendimento),0), 
		coalesce(max(cd_setor_atendimento),0) 
	into STRICT	nr_sequencia_w, 
		nr_atendimento_w, 
		cd_setor_atendimento_w 
	from	material_atend_paciente 
	where	nr_seq_gedipa_etapa_disp = nr_seq_etapa_prod_disp_w;
 
	if (nr_sequencia_w > 0) then 
 
		if (nr_atendimento_w > 0) then 
			select	ie_fim_conta, 
				dt_fim_conta 
			into STRICT	ie_fim_conta_w, 
				dt_fim_conta_w 
			from	atendimento_paciente 
			where	nr_atendimento = nr_atendimento_w;
			 
			if (ie_fim_conta_w = 'F') and (dt_fim_conta_w IS NOT NULL AND dt_fim_conta_w::text <> '') then 
				CALL WHEB_MENSAGEM_PCK.exibir_mensagem_abort(233434);
			end if;
		end if;
 
		CALL voltar_item_pendente_prescr(nr_sequencia_w, nm_usuario_p);
		 
		CALL excluir_glosa_valor_mat(nr_sequencia_w, nm_usuario_p);
		 
		update 	gedipa_etapa_prod_disp 
		set 	dt_conta  = NULL 
		where 	nr_sequencia = nr_seq_etapa_prod_disp_w;
	end if;
 
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
