SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gedipa_gerar_devolucao ( nr_seq_etiqueta_p text, nr_seq_area_prep_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
					 
nr_seq_etapa_hor_w bigint;
nr_seq_reaprov_w bigint;
nr_seq_etiqueta_w prescr_mat_hor.nr_seq_etiqueta%type;
ie_ja_devolvido_w	varchar(1);

c01 CURSOR FOR 
	SELECT 	b.nr_sequencia 
	from 	prescr_mat_hor a, gedipa_etapa_hor b 
	where 	a.nr_seq_etiqueta = nr_seq_etiqueta_w 
	and 	a.nr_sequencia = nr_seq_horario 
	and 	a.ie_agrupador <> 2;

 

BEGIN 
 
nr_seq_etiqueta_w := obter_etiqueta_barras_frac(nr_seq_etiqueta_p);
 
select	coalesce(max('S'), 'N') 
into STRICT	ie_ja_devolvido_w 
from	gedipa_reaproveitamento 
where	nr_seq_etiqueta = nr_seq_etiqueta_w;
 
if (ie_ja_devolvido_w = 'S') then 
	--Este item j√° foi devolvido. 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(343824);
end if;
 
open c01;
loop 
fetch c01 into	nr_seq_etapa_hor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	select 	nextval('gedipa_reaproveitamento_seq') 
	into STRICT 	nr_seq_reaprov_w 
	;
	 
	if (nr_seq_etapa_hor_w IS NOT NULL AND nr_seq_etapa_hor_w::text <> '') then 
		begin 
		insert into gedipa_reaproveitamento( 
			nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_seq_area_prep, 
			nr_seq_etapa_hor, 
			nr_seq_etiqueta, 
			dt_devolucao, 
			nm_usuario_devolucao, 
			ie_status_devolucao 
			) 
		values ( 
			nr_seq_reaprov_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_area_prep_p, 
			nr_seq_etapa_hor_w, 
			nr_seq_etiqueta_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			'D' 
			);
		end;
	end if;	
	end loop;
	close c01;
	 
commit;
 
CALL gedipa_estorn_bai_pres_mat(nr_seq_etiqueta_w, nm_usuario_p);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
