CREATE OR REPLACE PROCEDURE gedipa_separar_etapa ( nr_seq_etapa_p bigint, nm_usuario_p text) AS $body$
DECLARE

				
ie_higienizacao_w		adep_area_prep.ie_higienizacao%type;
nr_sequencia_w			adep_area_prep.nr_sequencia%type;	
				

BEGIN

update	gedipa_etapa_producao
set 	ie_status_etapa 	= 'S',
		nm_usuario 			= nm_usuario_p
where	nr_sequencia 		= nr_seq_etapa_p;

commit;

select	coalesce(max(ie_higienizacao),'S')				
into STRICT	ie_higienizacao_w		
from	adep_area_prep
where	nr_sequencia in (	SELECT	max(nr_seq_area_prep)
							from	gedipa_etapa_producao
							where	nr_sequencia	= nr_seq_etapa_p);
							
if (ie_higienizacao_w = 'N') then
	CALL gedipa_higienizar_etapa(nr_seq_etapa_p, nm_usuario_p);		
end if;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
