CREATE OR REPLACE PROCEDURE ged_substituir_mat_barras ( CD_MATERIAL_ATUAL_P bigint, CD_MATERIAL_SUBST_P bigint, QT_MATERIAL_SUBST_P bigint, DS_MATERIAL_SUBST_P text, QT_MATERIAL_P bigint DEFAULT 0, SL_MATERIAL_P bigint DEFAULT 0, NR_ID_P bigint  DEFAULT NULL) AS $body$
BEGIN
  IF ((coalesce(nr_id_p,0) > 0) AND (coalesce(cd_material_atual_p,0) > 0)) THEN
    IF (qt_material_p  > 0) THEN
      BEGIN
        UPDATE material_wbarras
        SET cd_material = cd_material_subst_p,
          qt_material   = qt_material_subst_p * qt_material_p ,
          ds_material   = ds_material_subst_p,
          sl_material   = qt_material_subst_p * sl_material_p
        WHERE nr_id     = nr_id_p
        AND cd_material = cd_material_atual_p;
      END;
    ELSE
      BEGIN
        UPDATE material_wbarras
        SET cd_material = cd_material_subst_p,
          qt_material   = qt_material_subst_p,
          ds_material   = ds_material_subst_p,
          sl_material   = qt_material_subst_p
        WHERE nr_id     = nr_id_p
        AND cd_material = cd_material_atual_p;
      END;
    END IF;
    COMMIT;
  END IF;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
