CREATE OR REPLACE PROCEDURE gel_alterar_antecipacao_result ( cd_pessoa_solic_p text, dt_antecipacao_p timestamp, ds_observacao_p text, nr_prescricao_p bigint, nr_seq_prescricao_p bigint, nr_seq_pedido_p bigint, nm_usuario_p text, ie_alteracao_p text) AS $body$
DECLARE


/*
ie_alteracao_p:
- R : Registrar
- A : Confirmar
- C : Cancelar
*/
nr_sequencia_w 			bigint;
dt_antecipacao_w		timestamp;


BEGIN

if (ie_alteracao_p = 'R') then

	select	nextval('prescr_proc_pedido_entrega_seq')
	into STRICT	nr_sequencia_w
	;

	insert into prescr_proc_pedido_entrega(nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_prescricao,
						nr_seq_prescricao,
						dt_antecipacao,
						cd_pessoa_solic,
						ds_observacao)
				values (nr_sequencia_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_prescricao_p,
					nr_seq_prescricao_p,
					dt_antecipacao_p,
					cd_pessoa_solic_p,
					substr(ds_observacao_p,1,255)
					);

elsif (ie_alteracao_p = 'A') then

	select	dt_antecipacao
	into STRICT	dt_antecipacao_w
	from	prescr_proc_pedido_entrega
	where	nr_sequencia = nr_seq_pedido_p;

	update 	 prescr_proc_pedido_entrega
	set	dt_confirmacao = clock_timestamp(),
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_pedido_p;

	update  	prescr_procedimento
	set	dt_resultado = dt_antecipacao_w,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_seq_prescricao_p;

elsif (ie_alteracao_p = 'C') then

	update  	prescr_proc_pedido_entrega
	set	dt_cancelamento = clock_timestamp(),
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_pedido_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
