CREATE OR REPLACE PROCEDURE gel_confirmar_receb_malote ( nr_seq_malote_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
SELECT	nr_seq_laudo,
	nr_seq_result_lab
from	envelope_laudo_item a,
	malote_envelope_item b
where	a.nr_seq_envelope = b.nr_seq_envelope
and	b.nr_seq_malote = nr_seq_malote_p;

nr_Seq_laudo_w			bigint;
nr_seq_result_lab_w		bigint;


BEGIN

update	malote_envelope_laudo
set	dt_recebimento_malote 	= clock_timestamp(),
	nm_usuario_receb_malote = nm_usuario_p,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p,
	nr_seq_status_malote	= 50
where	nr_sequencia		= nr_seq_malote_p;

open C01;
loop
fetch C01 into
	nr_seq_laudo_w,
	nr_seq_result_lab_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL gel_gravar_historico_envelope(nr_seq_laudo_w, nr_seq_result_lab_w, null, nr_seq_malote_p, 'MC', nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
