CREATE OR REPLACE PROCEDURE gel_desvincular_env_malote (nr_seq_envelope_p bigint, nr_seq_malote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_laudo_w			bigint;
nr_seq_result_lab_w		bigint;

ie_atualizar_w			varchar(1) := 'N';

C01 CURSOR FOR
SELECT	nr_seq_laudo,
	nr_seq_result_lab
from	envelope_laudo_item
where	nr_seq_envelope = nr_seq_envelope_p;


BEGIN

/*  Deletando o registro de malote do envelope*/

delete	FROM malote_envelope_item
where	nr_seq_envelope	= nr_seq_envelope_p;

/* Voltando o envelope para Conferido */

update	envelope_laudo
set	dt_saida_entrega 		 = NULL,
	nm_usuario_saida_entrega 	 = NULL,
	dt_atualizacao 			= clock_timestamp(),
	nm_usuario 			= nm_usuario_p,
	nr_seq_status_envelope 		= 15
where	nr_sequencia 			= nr_seq_envelope_p;

select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ie_atualizar_w
from	malote_envelope_item
where	nr_seq_malote	= nr_seq_malote_p
and	coalesce(dt_entrega::text, '') = '';

if (ie_atualizar_w = 'S') then
	update	malote_envelope_laudo
	set	dt_entrega = clock_timestamp(),
		nm_usuario_entrega = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		nr_seq_status_malote = 30
	where	nr_sequencia     = nr_seq_malote_p;
end if;

/* Gravar histórico da ação */

open C01;
loop
fetch C01 into
	nr_seq_laudo_w,
	nr_seq_result_lab_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL gel_gravar_historico_envelope(nr_seq_laudo_w, nr_seq_result_lab_w, nr_seq_envelope_p, nr_seq_malote_p, 'DVM', nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
