CREATE OR REPLACE PROCEDURE generate_anzics_data () AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	a.nr_atendimento,
		a.nr_seq_interno nr_seq_atepacu,
		coalesce(obter_idade_pf(c.cd_pessoa_fisica,clock_timestamp(),'A'),0) qt_idade
	from	atend_paciente_unidade a,
		setor_atendimento b,
		atendimento_paciente c
	where	a.cd_setor_atendimento = b.cd_setor_atendimento
	and	a.nr_atendimento = c.nr_atendimento
	and	coalesce(c.dt_alta::text, '') = ''
	and	b.cd_classif_setor = 4
	and	a.ie_passagem_setor = 'N'
	and	not exists (	SELECT	1
					from	atend_anzics x
					where	x.nr_seq_atepacu	= a.nr_seq_interno)
	order by  a.dt_entrada_unidade;
	
nr_idade_w	bigint;
BEGIN

nr_idade_w := Obter_param_Usuario(281, 1542, obter_perfil_ativo, obter_usuario_ativo, wheb_usuario_pck.get_cd_estabelecimento, nr_idade_w);


for r_c01 in c01 loop
	begin

	if (r_c01.qt_idade >= nr_idade_w) then
		CALL generate_atend_anzics(r_C01.nr_atendimento,r_C01.nr_seq_atepacu,'Tasy');
	
	end if;
	
	end;
end loop;

end;
