CREATE OR REPLACE PROCEDURE generate_cid_med_certificate ( nr_seq_atestado_p bigint, ds_cid_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ie_default_table_p text default 'S', nr_atendimento_p text default null) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
