++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
c_doenca CURSOR FOR
	SELECT	cd_doenca_cid
	from	cid_doenca
	where	obter_se_contido_char(cd_doenca_cid, ds_cid_p) = 'S';

c_doenca_w	c_doenca%rowtype;


BEGIN
OPEN c_doenca;
loop
FETCH c_doenca INTO c_doenca_w;
EXIT WHEN NOT FOUND; /* apply on c_doenca */
	begin
    if (ie_default_table_p = 'S') then
      insert into cid_atestado_paciente(   nr_sequencia,
            cd_doenca,
            nm_usuario,
            dt_atualizacao,
            nr_seq_atestado_paciente)
      values (   nextval('cid_atestado_paciente_seq'),
            c_doenca_w.cd_doenca_cid,
            nm_usuario_p,
            clock_timestamp(),
            nr_seq_atestado_p);
    else
      insert into kv_cuidado_pali_diag(
        nr_sequencia,
        cd_doenca,
        nm_usuario,
        nm_usuario_nrec,
        dt_atualizacao,
        dt_atualizacao_nrec,
        nr_seq_cuidado_paliat,
        nr_atendimento
      ) values (
        nextval('kv_cuidado_pali_diag_seq'),
        c_doenca_w.cd_doenca_cid,
        nm_usuario_p,
        nm_usuario_p,
        clock_timestamp(),
        clock_timestamp(),
        nr_seq_atestado_p,
        nr_atendimento_p
      );

    end if;
	end;
end loop;
CLOSE c_doenca;

commit;

end;
$body$
