CREATE OR REPLACE PROCEDURE generate_columns_dechar ( nr_sequencia_p bigint, nr_dechar_rule_p bigint, nm_table_p text, nm_usuario_p text) AS $body$
DECLARE


nr_custom_algorithm_w	custom_algorithm.nr_sequencia%type;

c_custom_algorithm_params CURSOR FOR
	SELECT 	p.nr_sequencia
	from	custom_algorithm s,
			column_custom_algorithm p
	where	s.nr_sequencia = nr_custom_algorithm_w
	and		p.nr_custom_algorithm = s.nr_sequencia
	and		coalesce(s.ie_situacao, 'A') = 'A';


BEGIN
if (nr_dechar_rule_p IS NOT NULL AND nr_dechar_rule_p::text <> '') and (nm_table_p IS NOT NULL AND nm_table_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	max(nr_custom_algorithm)
	into STRICT	nr_custom_algorithm_w
	from	decharacterization_rule
	where	nr_sequencia = nr_dechar_rule_p;

	if (nr_custom_algorithm_w IS NOT NULL AND nr_custom_algorithm_w::text <> '') then
		begin
		open c_custom_algorithm_params;
		loop
		fetch c_custom_algorithm_params into
			nr_custom_algorithm_w;
		EXIT WHEN NOT FOUND; /* apply on c_custom_algorithm_params */
			insert into column_decharacterization(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nm_table,
				nm_attribute,
				nr_table_dechar,
				nr_column_custom_alg
			) values (
				nextval('column_decharacterization_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nm_table_p,
				null,
				nr_sequencia_p,
				nr_custom_algorithm_w
			);
		end loop;
		commit;
		end;
	end if;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
