SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE generate_defect_so (nm_request_user_p text, nm_exec_user_p text, cd_version_p text, ie_platform_p text, ds_text_hist_default_p text, ds_link_build_error_p text, nr_seq_service_order_p INOUT bigint) AS $body$
DECLARE


nr_seq_ordem_w        man_ordem_servico.nr_sequencia%type;
cd_request_user_w    usuario.cd_pessoa_fisica%type;
ds_brief_damage_w        man_ordem_servico.ds_dano_breve%type;
ds_damage_w        man_ordem_servico.ds_dano%type;
nr_seq_gerencia_w    grupo_desenvolvimento.nr_seq_gerencia%type;
ie_classification_w    man_ordem_servico.ie_classificacao%type := 'E';
nr_seq_grupo_desenv_w    man_ordem_servico.nr_seq_grupo_des%type;
nm_exec_user_w        usuario.nm_usuario%type := nm_exec_user_p;
nm_request_user_w    usuario.nm_usuario%type := nm_request_user_p;
nm_analyst_user_w        usuario.nm_usuario%type;
nm_manager_user_w        usuario.nm_usuario%type;
ie_platform_w       man_ordem_servico.ie_plataforma%type := ie_platform_p;

BEGIN

select 'Build da versão '||cd_version_p||' com erro de compilação ' into STRICT ds_brief_damage_w;
select 'Erro de commit quebrou o build da versão '||cd_version_p||' - '||CASE WHEN ie_platform_w='J' THEN 'Java' WHEN ie_platform_w='H' THEN 'HTML5' WHEN ie_platform_w='D' THEN 'Delphi' WHEN ie_platform_w='T' THEN 'TWS'  ELSE null END
||' Link: '||ds_link_build_error_p into STRICT ds_damage_w;

nr_seq_ordem_w := GENERATE_SO(nm_request_user_w, nm_exec_user_w, ie_classification_w, ds_brief_damage_w, ds_damage_w, ds_text_hist_default_p, ie_platform_w, nr_seq_ordem_w);

update man_ordem_servico set ie_plataforma = ie_platform_w where nr_sequencia = nr_seq_ordem_w;

select  us.nm_usuario nm_usuario_gerente,
        gd.nm_usuario_lider
into STRICT 	nm_manager_user_w, nm_analyst_user_w
from    gerencia_wheb gw
inner join grupo_desenvolvimento gd on (gd.nr_seq_gerencia = gw.nr_sequencia)
inner join usuario_grupo_des gu on (gu.nr_seq_grupo = gd.nr_sequencia)
inner join usuario us ON (us.cd_pessoa_fisica = gw.cd_responsavel)
where gu.nm_usuario_grupo = nm_exec_user_w
and gd.ie_situacao = 'A';

nr_seq_service_order_p := nr_seq_ordem_w;

insert into man_incidental_build(
nr_sequencia,
dt_atualizacao,
nm_usuario,
dt_atualizacao_nrec,
nm_usuario_nrec,
nr_seq_ordem_serv,
nm_gerente,
nm_analista,
ie_gerou_excel,
cd_versao,
ie_plataforma,
ie_origem)
values (
nextval('man_incidental_build_seq'),
clock_timestamp(),
nm_request_user_w,
clock_timestamp(),
nm_request_user_w,
nr_seq_ordem_w,
nm_manager_user_w,
nm_analyst_user_w,
'N',
cd_version_p,
ie_platform_p,
'B');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
