SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE generate_dvr_request_data ( nr_interno_conta_p bigint, --account number patient billing
 cd_estabelecimento_p bigint, nm_usuario_p text, cd_error_string_p INOUT text) AS $body$
DECLARE

nr_seq_dvr_w		dva_claim.nr_sequencia%type;
cd_payee_provider_w	dva_claim.cd_payee_provider%type;
cd_claim_w		dva_claim.cd_claim%type;
dt_claim_w		dva_claim.dt_certified%type;
nr_seq_transaction_w    dva_claim.nr_seq_transaction%type;
request_sts		varchar(500);
nr_seq_history_claim_w	eclipse_claim_history.nr_sequencia%type;	
nr_seq_w 		dva_claim.NR_SEQUENCIA%type;


BEGIN

select	nextval('dvr_request_seq')
into STRICT	nr_seq_dvr_w 
;

select 	max(NR_SEQUENCIA)
into STRICT 	nr_seq_w
from 	dva_claim
where 	nr_interno_conta = nr_interno_conta_p;

select	max(cd_payee_provider)	payee_provider_val,
	max(cd_claim)	claim_val,
	max(nr_seq_transaction)	transaction_id,
	max(dt_atualizacao)	claim_data
into STRICT	cd_payee_provider_w,
	cd_claim_w,
	nr_seq_transaction_w,
	dt_claim_w
from	dva_claim
where	NR_SEQUENCIA = nr_seq_w
order by NR_SEQUENCIA desc;

if (coalesce(cd_payee_provider_w::text, '') = '' ) then
		cd_error_string_p := cd_error_string_p
                              || wheb_mensagem_pck.get_texto(1104644)
                              ||'##';
end if;

if (coalesce(cd_claim_w::text, '') = '' ) then
		cd_error_string_p := cd_error_string_p
                              || wheb_mensagem_pck.get_texto(1104650)
                              ||'##';
end if;
if (coalesce(dt_claim_w::text, '') = '' ) then
		cd_error_string_p := cd_error_string_p
                              || wheb_mensagem_pck.get_texto(1104651)
                              ||'##';

end if;

if (coalesce(cd_error_string_p::text, '') = '') then
	begin
	
	insert	into dvr_request(nr_sequencia ,
				dt_atualizacao ,
				nm_usuario ,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_payee_provider,
				cd_claim,
				dt_claim,
				nr_seq_transaction)
	values (nr_seq_dvr_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_payee_provider_w,
				cd_claim_w,
				dt_claim_w,
				nr_seq_transaction_w);
				
	select	nextval('eclipse_claim_history_seq')
	into STRICT	nr_seq_history_claim_w 
	;
	
	insert	into	eclipse_claim_history(	
			nr_sequencia  	,
			nm_usuario	,
			dt_atualizacao 	,
			ds_historico	, 
			nr_interno_conta
		)
	values (	
			nr_seq_history_claim_w,
			nm_usuario_p,
			clock_timestamp(),
			wheb_mensagem_pck.get_texto(1105899) , 
			nr_interno_conta_p
		);
	commit;
	request_sts :=	'{' || chr(10) ||'"transactionId":"' || nr_seq_transaction_w
			||'",'|| chr(10) || '"userName":"' ||nm_usuario_p|| '"}';
	CALL EXECUTE_BIFROST_INTEGRATION(189, request_sts);
	commit;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
