SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE generate_function_checklist ( cd_funcao_p bigint, nm_usuario_p text, nr_versao_p bigint default null) AS $body$
DECLARE


C01 CURSOR FOR
		SELECT	nr_Sequencia,
				ie_tipo_componente
		from	qr_objeto_schematic
		where	(ie_tipo_componente IS NOT NULL AND ie_tipo_componente::text <> '')
		and		cd_funcao = cd_funcao_p
		and		nr_versao = nr_versao_p
		order by 1;


c01_w	c01%rowtype;


BEGIN

if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		insert into qr_checklist_funcao(
					nr_Sequencia,
					nr_seq_checklist_item,
					nr_Seq_objeto,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec)
		SELECT 		nextval('qr_checklist_funcao_seq'),
					a.nr_Seq_checklist_item,
					c01_w.nr_sequencia,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p
		from		qr_checklist_componente a
		where		a.ie_tipo_componente	= c01_w.ie_tipo_componente
		and		not exists (SELECT 	1
							from 	qr_checklist_funcao b
							where 	b.nr_seq_checklist_item = a.nr_sequencia
							and 	b.nr_seq_objeto = c01_w.nr_Sequencia  LIMIT 1);


		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
