SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE generate_history_classif_cad ( nr_seq_ordem_p man_ordem_servico.nr_sequencia%type, nr_seq_classif_cad_p man_ordem_servico.nr_seq_classif_cad%type, nm_usuario_p man_ordem_servico.nm_usuario%type) AS $body$
DECLARE


ds_mensagem_w		man_ordem_serv_tecnico.ds_relat_tecnico%type;
ds_relat_tecnico_w	man_ordem_serv_tecnico.ds_relat_tecnico%type;
ds_tipo_w		man_ordem_serv_tecnico.ds_relat_tecnico%type;
cd_exp_item_w		man_ordem_serv_quest_item.cd_exp_item%type;
new_line_w			man_ordem_servico.ds_dano%type;

nr_seq_idioma_usuar_w 		man_ordem_serv_tecnico.nr_sequencia%type 	:= wheb_usuario_pck.get_nr_seq_idioma;
nr_seq_tipo_padrao_w 		man_tipo_hist.nr_sequencia%type 		:= 288;
	
c01 CURSOR FOR
	SELECT	mosi.cd_exp_item cd_exp_item
	from	man_ordem_serv_quest_item mosi
	where	mosi.nr_seq_quest_ws in (
					SELECT	mosws.nr_sequencia
					from	man_ordem_serv_quest_ws mosws
					where	mosws.dt_inicio_vigencia < clock_timestamp()
					and (coalesce(mosws.dt_fim_vigencia::text, '') = '' or mosws.dt_fim_vigencia > clock_timestamp())
					and	(mosws.dt_liberacao IS NOT NULL AND mosws.dt_liberacao::text <> '')
					and	mosws.nr_seq_classif_cad = nr_seq_classif_cad_p
					)
	order by mosi.nr_seq_apresentacao;


BEGIN
new_line_w := chr(13) || chr(10);

if (nr_seq_classif_cad_p IS NOT NULL AND nr_seq_classif_cad_p::text <> '') then

	open c01;
	loop fetch c01 into
		cd_exp_item_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	ds_relat_tecnico_w := ds_relat_tecnico_w || new_line_w || obter_desc_expressao_idioma(cd_exp_item_w, 'N/A', nr_seq_idioma_usuar_w) || ':' || new_line_w;

	end loop;
	close c01;

	if (ds_relat_tecnico_w IS NOT NULL AND ds_relat_tecnico_w::text <> '') then
		select	obter_desc_expressao_idioma(cd_exp_tipo, ds_tipo, nr_seq_idioma_usuar_w)
		into STRICT	ds_tipo_w
		from	man_tipo_hist
		where	nr_sequencia = nr_seq_tipo_padrao_w;

		if (ds_tipo_w IS NOT NULL AND ds_tipo_w::text <> '') then
			ds_mensagem_w	:= ds_tipo_w || CHR(10) || CHR(13) || CHR(10) || CHR(13);
		end if;
		ds_mensagem_w := ds_mensagem_w || ds_relat_tecnico_w;
		
		CALL man_gravar_historico_ordem(
				nr_seq_ordem_serv_p 	=> nr_seq_ordem_p,
				dt_liberacao_p 		=> clock_timestamp(),
				ds_relat_tecnico_p 	=> ds_mensagem_w,
				ie_origem_p 		=> 'I',
				nr_seq_tipo_p 		=> nr_seq_tipo_padrao_w,
				nm_usuario_p 		=> nm_usuario_p);
	end if;
	
	commit;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE generate_history_classif_cad ( nr_seq_ordem_p man_ordem_servico.nr_sequencia%type, nr_seq_classif_cad_p man_ordem_servico.nr_seq_classif_cad%type, nm_usuario_p man_ordem_servico.nm_usuario%type) FROM PUBLIC;

