CREATE OR REPLACE PROCEDURE generate_hosp_plan_pendency ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE

					
is_exists_w varchar(1) := 'S';


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT 	is_exists_w
from 	PEP_ITEM_PENDENTE
where 	IE_TIPO_REGISTRO  ='HOSP'
and     NR_ATENDIMENTO = nr_atendimento_p
and     nm_usuario = nm_usuario_p;

 if (is_exists_w   = 'N' and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '')) then
	insert into PEP_ITEM_PENDENTE(	nr_sequencia,
						dt_atualizacao,
						nm_usuario ,
						dt_atualizacao_nrec ,
						nm_usuario_nrec ,
						nr_atendimento ,
						cd_pessoa_fisica,
						NR_SEQ_REGISTRO,
						NR_SEQ_ITEM_PRONT ,
						IE_TIPO_PENDENCIA,
						IE_TIPO_REGISTRO,
						DT_REGISTRO)
				values (nextval('pep_item_pendente_seq') ,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_atendimento_p,
						cd_pessoa_fisica_p,
						nr_atendimento_p,
						398,  -- Consents(Hospitalization plan)
						'L',
						'HOSP',
						clock_timestamp());

	end if;
	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
