CREATE OR REPLACE PROCEDURE generate_icunsw_cert_num (nr_sequencia_p bigint) AS $body$
DECLARE


nr_seq_cert_w  bigint;
nr_atendimento_w bigint;



BEGIN

select nr_atendimento into STRICT nr_atendimento_w from icunsw where nr_sequencia = nr_sequencia_p;

select (count(1) +1) into STRICT nr_seq_cert_w  from ICUNSW
where nr_atendimento = nr_atendimento_w
and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and ie_situacao ='A'
and coalesce(dt_inativacao::text, '') = '';

Update icunsw set nr_certificate = nr_seq_cert_w
where nr_sequencia = nr_sequencia_p;

commit;

end;
