SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE generate_medical_diag_default (nr_atendimento_p bigint, nm_usuario_p text, ds_retorno_abort_p INOUT text) AS $body$
DECLARE


ie_diagnostico_admissao_w	classificacao_diagnostico.ie_diagnostico_admissao%type;
qt_medic_diag_w				bigint;
cd_estabelecimento_w		medic_diagnostico_doenca.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;

C01 CURSOR FOR
	SELECT	a.nr_seq_interno,
			a.dt_diagnostico,
			cd_doenca
	from	diagnostico_doenca	a
	where	a.nr_atendimento	= nr_atendimento_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_p))
	and		coalesce(a.ie_situacao,'A') = 'A'
	and		coalesce(a.dt_inativacao::text, '') = ''
	order by 2 desc;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	classificacao_diagnostico
	where	coalesce(ie_situacao,'A') 	= 'A'
	and		coalesce(ie_valor_padrao,'N')		= 'S';
BEGIN

for r_c01 in C01 loop
	begin

	for r_c02 in C02 loop
		begin
		select	count(1)
		into STRICT	qt_medic_diag_w
		from	medic_diagnostico_doenca a,
				diagnostico_doenca b
		where   a.cd_doenca				= b.cd_doenca
		and		a.nr_atendimento		= b.nr_atendimento
		and		a.dt_diagnostico		= b.dt_diagnostico
		and		a.nr_seq_classificacao	= r_c02.nr_sequencia
		and		b.nr_seq_interno 		= r_c01.nr_seq_interno;

		if (qt_medic_diag_w = 0) then

			SELECT * FROM generate_medical_diag_disease(
				nr_atendimento_p, r_c01.cd_doenca, r_c02.nr_sequencia, r_c01.dt_diagnostico, null, nm_usuario_p, cd_estabelecimento_w, ie_diagnostico_admissao_w, ds_retorno_abort_p) INTO STRICT ie_diagnostico_admissao_w, ds_retorno_abort_p;

		end if;

		end;
	end loop;

	end;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
