CREATE OR REPLACE PROCEDURE generate_multi_schedule_cons (nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_agenda_p bigint, hr_agenda_p timestamp, nr_seq_ageint_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_agenda_w			bigint;

dt_agenda_w			agenda_consulta.dt_agenda%type;
nr_seq_agenda_w			agenda_consulta.nr_sequencia%type;
nr_seq_ageint_w			agenda_integrada.nr_sequencia%type;
nr_seq_status_w			agenda_integrada.nr_seq_status%type;


BEGIN

select	coalesce(max(nr_sequencia),0),
	max(dt_agenda)
into STRICT	nr_seq_agenda_w,
	dt_agenda_w
from    agenda_consulta
where   cd_agenda       = cd_agenda_p
and     dt_agenda       = hr_agenda_p;

select	coalesce(count(*),0)
into STRICT	qt_agenda_w
from	agenda_consulta
where	nr_sequencia			= nr_seq_agenda_w
and	coalesce(ie_status_agenda,'L')	= 'L';

if (qt_agenda_w > 0) then
	update	agenda_paciente
	set	ie_status_agenda	= 'N',
		cd_pessoa_fisica	= cd_pessoa_fisica_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nr_atendimento 		= nr_atendimento_p
	where	nr_sequencia		= nr_seq_agenda_w;


	select	min(nr_sequencia)
	into STRICT	nr_seq_status_w
	from 	agenda_integrada_status
	where 	ie_situacao	= 'A'
	and 	ie_status_tasy	= 'AG';

	update	agenda_integrada
	set	dt_inicio_agendamento	= dt_agenda_w,
		nr_seq_status		= nr_seq_status_w
	where	nr_sequencia		= nr_seq_ageint_p;

	update  agenda_integrada_item
	set	nr_seq_agenda_cons	= nr_seq_agenda_w
	where   nr_seq_agenda_int	= nr_seq_ageint_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
