CREATE OR REPLACE PROCEDURE generate_opsm_authorization (nr_sequencia_p bigint) AS $body$
DECLARE

																																		
ie_gerar_itens_opme_w				varchar(15) := 'N';
ie_gerar_autor_cirur_opme_w 	  	varchar(15) := 'N';
ie_gerar_neces_autor_opme_w			varchar(15) := 'N';
nm_usuario_w						usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
cd_perfil_w							perfil.cd_perfil%type	:= wheb_usuario_pck.get_cd_perfil;
cd_estabelecimento_w				estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
ie_gerar_autor_w					agenda_pac_opme.ie_gerar_autor%type;
nr_seq_agenda_w						agenda_paciente.nr_sequencia%type;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	select	max(ie_gerar_autor),
			max(nr_seq_agenda)
	into STRICT	ie_gerar_autor_w,
			nr_seq_agenda_w			
	from	agenda_pac_opme
	where	nr_sequencia = nr_sequencia_p;		

	ie_gerar_itens_opme_w := obter_param_usuario(871, 85, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_gerar_itens_opme_w);
	ie_gerar_autor_cirur_opme_w := obter_param_usuario(871, 388, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_gerar_autor_cirur_opme_w);
	ie_gerar_neces_autor_opme_w := obter_param_usuario(871, 396, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_gerar_neces_autor_opme_w);

	if (ie_gerar_neces_autor_opme_w = 'O') and (ie_gerar_itens_opme_w <> 'N') then
		CALL gerar_autor_cirurgia_opme(nr_seq_agenda_w, '0', cd_estabelecimento_w, nm_usuario_w, ie_gerar_itens_opme_w);
	end if;

	if (coalesce(ie_gerar_autor_w, 'XPTO') <> 'N') and (ie_gerar_autor_cirur_opme_w = 'S') then
		CALL gerar_autor_regra(null, null, null, null, null, null, 'AP', nm_usuario_w, nr_seq_agenda_w, null, null, null, nr_sequencia_p, null, null, null, null);
	end if;
end if;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
